mapper.select:
WITH FacilityGroups AS ( SELECT fclty_nm, COUNT(*) as program_count, ROW_NUMBER() OVER ( ORDER BY
MAX(COALESCE(( SELECT COUNT(*) FROM program_likes WHERE program_id = fp.id ), 0)) DESC
MIN(progrm_end_de) ASC
MAX(created_at) DESC
) as facility_rank FROM facility_program fp WHERE 1=1
AND ctprvn_nm = #{cityName}
AND signgu_nm = #{districtName}
AND progrm_trget_nm = #{programTargetName}
AND (
progrm_nm LIKE CONCAT('%', '수영', '%')
progrm_nm LIKE CONCAT('%', '골프', '%')
progrm_nm LIKE CONCAT('%', '탁구', '%')
progrm_nm LIKE CONCAT('%', '배드민턴', '%')
(progrm_nm LIKE CONCAT('%', '댄스', '%') OR progrm_nm LIKE CONCAT('%', '춤', '%'))
progrm_nm LIKE CONCAT('%', '필라테스', '%')
(progrm_nm LIKE CONCAT('%', '헬스', '%') OR progrm_nm LIKE CONCAT('%', '피트니스', '%'))
progrm_nm LIKE CONCAT('%', '테니스', '%')
progrm_nm LIKE CONCAT('%', '에어로빅', '%')
(progrm_nm LIKE CONCAT('%', '축구', '%') OR progrm_nm LIKE CONCAT('%', '풋살', '%'))
progrm_nm NOT LIKE CONCAT('%', '수영', '%') AND progrm_nm NOT LIKE CONCAT('%', '골프', '%') AND progrm_nm NOT LIKE CONCAT('%', '탁구', '%') AND progrm_nm NOT LIKE CONCAT('%', '배드민턴', '%') AND progrm_nm NOT LIKE CONCAT('%', '댄스', '%') AND progrm_nm NOT LIKE CONCAT('%', '춤', '%') AND progrm_nm NOT LIKE CONCAT('%', '필라테스', '%') AND progrm_nm NOT LIKE CONCAT('%', '헬스', '%') AND progrm_nm NOT LIKE CONCAT('%', '피트니스', '%') AND progrm_nm NOT LIKE CONCAT('%', '테니스', '%') AND progrm_nm NOT LIKE CONCAT('%', '에어로빅', '%')
)
AND
REGEXP_LIKE(progrm_estbl_wkday_nm, #{day})
AND progrm_prc >= #{minPrice}
AND progrm_prc <= #{maxPrice}
GROUP BY fclty_nm ) SELECT fp.id, fp.ctprvn_cd as cityCode, fp.ctprvn_nm as cityName, fp.signgu_cd as districtCode, fp.signgu_nm as districtName, fp.edc_goal_cn as educationGoalContent, fp.fclty_addr as facilityAddress, fp.fclty_nm as facilityName, fp.fclty_tel_no as facilityPhoneNumber, fp.fclty_ty_nm as facilityTypeName, fp.hmpg_url as homepageUrl, fp.ldr_qualfc_cn as leaderQualificationContent, fp.progrm_begin_de as programBeginDate, fp.progrm_end_de as programEndDate, fp.progrm_nm as programName, fp.progrm_estbl_wkday_nm as programOperationDays, fp.progrm_estbl_tizn_value as programOperationTime, fp.progrm_prc as programPrice, fp.progrm_prc_ty_nm as programPriceTypeName, fp.progrm_rcrit_nmpr_co as programRecruitNumber, fp.progrm_trget_nm as programTargetName, fp.progrm_ty_nm as programTypeName, fp.prtc_nmpr_partcpt_at as protectorParticipationYn, fp.safe_managt_cn as safetyManagementContent, fp.view_count as viewCount, fp.created_at as createdAt, COALESCE(( SELECT COUNT(*) FROM program_likes WHERE program_id = fp.id ), 0) as likeCount, fg.program_count FROM facility_program fp INNER JOIN FacilityGroups fg ON fp.fclty_nm = fg.fclty_nm WHERE fg.facility_rank BETWEEN #{start} + 1 AND #{start} + #{pageSize} ORDER BY fg.facility_rank, fp.created_at DESC;

SELECT COUNT(*)
FROM facility_program
WHERE 1=1

    AND ctprvn_nm = #{cityName}


    AND signgu_nm = #{districtName}


    AND progrm_trget_nm = #{programTargetName}


    AND progrm_estbl_wkday_nm IN

        #{day}




AND progrm_prc >= #{minPrice}




AND progrm_prc <= #{maxPrice}

mapper.update:
UPDATE facility_program
SET view_count = view_count + 1
WHERE id = #{programId}

mapper.select:
SELECT EXISTS(
SELECT 1
FROM program_likes
WHERE program_id = #{programId}
AND member_id = #{memberId}
) as exists_flag

mapper.delete:
DELETE FROM program_likes
WHERE program_id = #{programId}
AND member_id = #{memberId}

mapper.select:
SELECT COUNT(*)
FROM program_likes
WHERE program_id = #{programId}

SELECT COUNT(*)
FROM program_registers
WHERE program_id = #{programId}
